mapper.select:
SELECT t2.*,t3.created_time AS rewash_time,t4.operate_status,t4.operate_time FROM order_info AS t1 LEFT JOIN order_clothes AS t2 ON t1.order_id = t2.order_id LEFT JOIN order_clothes_rewash AS t3 ON t3.rewash_id = ( SELECT rewash_id FROM order_clothes_rewash WHERE clothes_num = t2.clothes_num ORDER BY created_time DESC LIMIT 1 ) LEFT JOIN order_clothes_operate AS t4 ON t4.operate_id = ( SELECT operate_id FROM order_clothes_operate WHERE clothes_num = t2.clothes_num AND operate_status = 1 ORDER BY operate_time DESC LIMIT 1 ) WHERE 1 = 1
AND t4.operate_status = #{operate}
AND t2.clothes_num IN
#{item}
